buildscript {    extra["kotlinVersion"] = "1.2.20-eap-33"    extra["kotlinRepo"] = "https://dl.bintray.com/kotlin/kotlin-eap"    repositories {        maven(url = extra["kotlinRepo"] as String)    }    dependencies {        classpath("org.jetbrains.kotlin:kotlin-gradle-plugin:${extra["kotlinVersion"] as String}")    }}allprojects {    repositories {        maven(url = rootProject.extra["kotlinRepo"] as String)        mavenCentral()    }}