plugins {    java}apply {    plugin("kotlin")}dependencies {    compile(project(":api"))    compile(project(":jvm"))    compile("org.jetbrains.kotlin:kotlin-script-util:${rootProject.extra["kotlinVersion"] as String}")    testCompile("junit:junit:4.12")    testRuntime("com.jcabi:jcabi-aether:0.10.1")    testRuntime("org.sonatype.aether:aether-api:1.13.1")    testRuntime("org.apache.maven:maven-core:3.0.3")}java.sourceSets["main"].java {    srcDir("src")}java.sourceSets["test"].java {    srcDir("test")}val test: Test by taskstest.apply {    workingDir = projectDir}